sect2.programlisting:
// Other (name omitted) vendor solution statement:
select * from (select * from Market where ticker = 'GOOG') retain 1 event
// The above is NOT an Esper statement

// Esper statement with the same semantics:
select * from MarketData(ticker='$').std:lastevent()
// ... or ...
select * from MarketData(ticker='$').win:length(1)

// Esper statement that DOES ***NOT*** HAVE the same semantics 
// No length window was used
select * from MarketData(ticker='$')

var stmt = epService.EPAdministrator.CreateEPL(stmtText);
var stmtEventType = stmt.EventType;
var symbolGetter = stmtEventType.GetGetter("symbol");
var avgPriceGetter = stmtEventType.GetGetter("avg(price)");
var eventHandler = delegate(Object sender, UpdateEventArgs e) {
	var symbol = (String) symbolGetter.get(e.NewEvents[0]);
	var volume = (long) volumeGetter.get(e.NewEvents[0]);
	// some more logic here
};

stmt.Events += eventHandler;

public void HandleEvents(Object sender, UpdateEventArgs e) {
    MarketData md = (MarketData) e.NewEvents[0].Underlying;
    // some more logic here
}

// Replace the following statement as it may not perform well select a.symbol, avg(a.value), avg(b.value) from MyEvent.win:length(100) a, MyEvent.win:length(50) b
// Fast version, first alternative select a.symbol, avg(a.value), avg(b.value) from MyEvent.win:length(100) a, MyEvent.win:length(50) b where a.symbol = b.symbol
// Fast version, second alternative select (select avg(value) from MyEvent.win:length(100)) as avgA, (select avg(value) from MyEvent.win:length(50)) as avgB, a.symbol from MyEvent.std:lastevent() a, MyEvent.std:lastevent() b
// Fast version, third alternative insert into StreamAvgA select symbol, avg(value) as avgA from MyEvent.win:length(100) insert into StreamAvgB select symbol, avg(value) as avgB from MyEvent.win:length(50) select a.symbol, avgA, avgB from StreamAvgA.std:lastevent() a, StreamAvgB.std:lastevent() b
